-- ----------------------------
-- Table structure for book
-- ----------------------------
DROP TABLE IF EXISTS `book`;
CREATE TABLE `book` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '图书编号',
  `name` varchar(20) NOT NULL COMMENT '书名',
  `label` varchar(255) NOT NULL COMMENT '标签',
  `authorId` varchar(13) NOT NULL COMMENT '作者编号',
  `typeId` int(11) NOT NULL COMMENT '类别编号',
  `wordNum` int(11) NOT NULL DEFAULT '0' COMMENT '字数',
  `collectionNum` int(11) NOT NULL DEFAULT '0' COMMENT '收藏数',
  `state` tinyint(2) NOT NULL DEFAULT '0' COMMENT '图书状态（0-审核中，1-新建，2-签约作品）',
  `profile` varchar(255) NOT NULL COMMENT '图书简介',
  `clickNum` int(11) NOT NULL DEFAULT '0' COMMENT '点击数',
  `cover` varchar(255) NOT NULL COMMENT '封面',
  `creationTime` datetime NOT NULL COMMENT '创建时间',
  `chapterId` int(11) NOT NULL DEFAULT '0' COMMENT '最新章节编号',
  `recommendNum` int(11) NOT NULL DEFAULT '0' COMMENT '推荐数',
  `isRecommend` tinyint(1) NOT NULL DEFAULT '0' COMMENT '主编推荐',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=18 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of book
-- ----------------------------
INSERT INTO `book` VALUES ('1', '武霸洪荒', '仙侠,洪荒', '18279668702', '2', '71560', '5', '1', '浩瀚无边的神荒大陆，万族林立，诸强并起，神魔共存。这里有着千古不绝的血脉，有着万古不灭的武魂，强大的人类武者，上天能摘星揽月，笑傲苍穹，下地能翻江倒海，威震乾坤，一笑能让山河失色，一怒更能让时空幻灭。一颗黑不溜秋的珠子，一部看似玄之又玄的内功心法，落在了一个落魄少年的身上，于是一段霸绝神荒的惊世传奇就这么开始了！', '213', 'http://oyn1b4wss.bkt.clouddn.com/1510709200688', '2017-09-27 06:34:00', '25', '543', '1');
INSERT INTO `book` VALUES ('4', '摸金校尉', '盗墓', '18279668702', '3', '0', '0', '1', '上世纪80年代初，胡八一、王胖子、大金牙三个原本见财起意的生意人，在一次遇险逃亡路上无意中得到传说中的西夏镇国之宝，并在逃命过程中遭遇种种危险经历，最后三人觉悟，决定对抗各路夺宝势力，保护国宝文物的惊天探险之旅。\n小说了展现了原本为不良生意人的几个小人物从见利忘义到遇险觉悟，保护国家文化遗产的故事，体现了小人物的爱国情怀。', '6', 'http://oyn1b4wss.bkt.clouddn.com/1509538016321', '2017-11-01 20:07:14', '0', '234', '1');
INSERT INTO `book` VALUES ('5', '观念的水位', '社会新闻，时事政治', '18279668702', '14', '0', '0', '1', '作为国内公共领域最重要的声音之一，刘瑜的文字向来拥趸众多，之前的作品更多关注美国民主，与中国相关度不高。而本书更多关注东亚、中东欧、南美洲以及非洲国家的政治与民主化，比如英国、俄罗斯、委内瑞拉、赞比亚等，这些国家的民主化进程参差不齐，而很多情况与现今中国有较强的可比性，这无疑让《观念的水位》更生动，更接地气。', '5', 'http://oyn1b4wss.bkt.clouddn.com/1510708709967', '2017-11-15 09:20:33', '0', '4', '1');
INSERT INTO `book` VALUES ('6', '30岁赚千万', '智慧，知识，财富', '18279668702', '32', '0', '0', '1', '现代社会，经济得到了很大的发展，财富在生活中的角色越来越重要，这就要求每个人都要具备创造财富的能力。因此，年轻人都在想方设法地赚取财富。然而，有的人成功赚取千万，有的人却一事无成，他们之间到底差距在哪里呢？财富的真相到底是什么呢？', '0', 'http://oyn1b4wss.bkt.clouddn.com/1510709351612', '2017-11-15 09:29:44', '0', '3', '1');
INSERT INTO `book` VALUES ('7', ' 为什么你买不起房', '宏观经济', '18279668702', '33', '0', '0', '1', '为什么北京、上海房价比大多数内地城市高十倍？过去十年北京房价涨幅为何比青岛多五倍？为什么同样城市房价却有差距？哪些城市更值得投资？\n限制土地供应的城市，房价必涨！政府部门继续过分集中于大城市，房价必涨！大企业和大专学校云集特定区域，房价必涨！由大开发商垄断市场而缺乏中小开发商参与，房价必涨！', '0', 'http://oyn1b4wss.bkt.clouddn.com/1510711197336', '2017-11-15 10:00:30', '0', '23', '1');
INSERT INTO `book` VALUES ('8', '不衰老不疲劳的生活', '中医,女性,养生', '18279668702', '17', '0', '0', '1', '衰老与疲劳是现代女性健康与美丽的大敌。\n《不衰老不疲劳的生活》是佟彤2009年出版的《不疲劳的生活》的全新修订升级版。作者佟彤集合十余年的中医实践经验，遍访全国顶尖妇科医师、权威中医专家，总结数万案例精华，在《不疲劳的生活》基础上，新增了关于女性抗衰老防疲劳的内容，详述了女性、肥胖者、劳心者、心脏弱等最易衰老、疲劳的四种人，以及皱纹、脱发、漏尿、湿重、暴肥、疼痛等八大衰老预兆，详细呈现蜂王浆、人参、三七、红景天、维生素等养颜圣品的功效用法', '0', 'http://oyn1b4wss.bkt.clouddn.com/1510711276853', '2017-11-15 10:02:09', '0', '78', '1');
INSERT INTO `book` VALUES ('9', '深入理解计算机网络', '计算机理论,硬件,软件开发,人工智能', '18279668702', '57', '0', '0', '1', '全国网管技能水平考试认证专家王达最新力作，51CTO技术社区鼎力推荐！\n结合最新技术阐述计算机网络的体系结构、工作原理，以及各种通信协议实现原理\n包含近600幅图表，通俗易懂，极大程度降低学习难度\n本书结合最新计算机网络技术，全面、系统、深入地阐述了计算机网络的体系结构、工作原理，以及各种通信协议实现原理。', '0', 'http://oyn1b4wss.bkt.clouddn.com/1510711403032', '2017-11-15 10:04:01', '0', '456', '0');
INSERT INTO `book` VALUES ('10', ' 飞鸟集（果麦经典）', '亚洲小说,印度小说', '18279668702', '48', '0', '0', '1', '1916年第一次世界大战期间，因获得诺贝尔文学奖而在国际上名声大噪的泰戈尔到访日本。他被那里的生活和风光深深吸引，并接触到日本古典短诗——俳句。那些罕见的短诗或许在他身上产生了影响。他应青年们的要求，在他们的扇子或签名簿上写下一些文字，这些零星的词句或短文后来集结成《飞鸟集》出版。', '0', 'http://oyn1b4wss.bkt.clouddn.com/1510711575378', '2017-11-15 10:06:58', '0', '44', '0');
INSERT INTO `book` VALUES ('11', ' 小王子', '外国儿童文学', '18279668702', '72', '0', '0', '1', '遥远星球上的小王子，与美丽而骄傲的玫瑰吵架负气出走，在各星球漫游中，小王子遇到了傲慢的国王、酒鬼、惟利是图的商人，死守教条的地理学家，最后来到地球上，试图找到治愈孤独和痛苦的良方。\n这时，他遇到一只奇怪的狐狸，于是奇妙而令人惊叹的事情发生了……\n《小王子》犹如透亮的镜子，照出了荒唐的成人世界。她在提醒我们，只有爱，才是最高的哲学，才是我们活下去的唯一理由。', '0', 'http://oyn1b4wss.bkt.clouddn.com/1510711681175', '2017-11-15 10:08:05', '0', '67', '0');
INSERT INTO `book` VALUES ('12', '如何高效学习', '通识读物', '18279668702', '68', '0', '0', '0', '一位因为快速学习而成名的神奇小子，他应用自己发明的学习方法，完成了10天搞定线性代数，1年学习4年MIT课程的“不可能任务”。本书就是对他学习方法的全面介绍，其中包括整体性学习策略的核心思想和具体技术，详细介绍了快速阅读法、流笔记法、比喻法、内在化等七大方法，并为高效学习提供了从生活到时间管理的整体解决方案。跟随作者，你也将成为高效学习的超级学霸。', '0', 'http://oyn1b4wss.bkt.clouddn.com/1510711728610', '2017-11-15 10:09:21', '0', '87', '0');
INSERT INTO `book` VALUES ('13', '昆虫记', '生物', '18279668702', '55', '0', '0', '0', '《昆虫记》也叫做《昆虫物语》、《昆虫学札记》和《昆虫世界》，英文名称是《The Records about Insects》，是法国杰出昆虫学家法布尔的传世佳作，亦是一部不朽的著作。它不仅是一部文学巨著，也是一部科学百科。它融合作者毕生研究成果和人生感悟于一炉，以人性关照虫性，又用虫性反观社会人生，将昆虫世界化作供人类获得知识、趣味、美感和思想的美文，这本书以忠实于法文原著整体风貌及表达特色为选择原则， 让世界读者首次领略昆虫们的日常生活习性以及特征的描述等。《', '1', 'http://oyn1b4wss.bkt.clouddn.com/1510711826252', '2017-11-15 10:10:53', '0', '55', '0');
INSERT INTO `book` VALUES ('15', '颠覆者', '人物传记', '18279668702', '10', '0', '0', '1', '本书记录了周鸿祎先生从幼年时代开始，直到今天四十余年的传奇经历。\n周鸿祎先生的成长几乎伴随了整个中国互联网的成长，从90年代上大学期间在校园里销售反病毒卡，再到离职北大方正后创立3721；从与CNNIC的战斗，到成为中国雅虎的总裁；从互联网第一口水战，到改变互联网格局的3Q大战；从免费杀毒到360成功在美国上市……勾勒出了一位互联网老兵近二十年的创业江湖路。', '3', 'http://oyn1b4wss.bkt.clouddn.com/1511488930653', '2017-11-24 10:03:20', '0', '0', '1');
INSERT INTO `book` VALUES ('16', ' 你无法抵达的时间', '科幻,作品集', '18279668702', '5', '0', '0', '0', '《你无法抵达的时间》是夏笳的中短篇小说精选集，收录从她早期的“银河奖”获奖作品《关妖精的瓶子》、《百鬼夜行街》，到近期发表的别具韵味的《龙马夜行》、《晚安忧郁》等共十六篇作品。', '3', 'http://oyn1b4wss.bkt.clouddn.com/1511668883947', '2017-11-26 12:02:05', '0', '0', '0');
INSERT INTO `book` VALUES ('17', '不成问题的问题', '经典,短篇,小说', '18279668702', '1', '0', '0', '0', '老舍《不成问题的问题》故事发生在抗战重庆树华农场，农场管理者丁场长管理能力不行，但哄人能力一流，精通人情世故，在他治理下，大家整日偷鸡打牌，混日子。树华农场颓势一片，冒牌青年艺术家秦妙斋也掺和进来。留学归来尤主任接管农场后，开始整治作风，大家感到了自律和科学带来的愉悦', '2', 'http://oyn1b4wss.bkt.clouddn.com/1511670485423', '2017-11-26 12:28:43', '0', '0', '0');

-- ----------------------------
-- Table structure for chapter
-- ----------------------------
DROP TABLE IF EXISTS `chapter`;
CREATE TABLE `chapter` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '章节编号',
  `bookId` int(11) NOT NULL COMMENT '图书编号',
  `name` varchar(20) NOT NULL COMMENT '章节名',
  `content` text NOT NULL COMMENT '内容',
  `wordNum` int(11) NOT NULL DEFAULT '0' COMMENT '字数',
  `state` tinyint(1) NOT NULL DEFAULT '0' COMMENT '状态（0-审核中，1-公共，2-VIP）',
  `time` datetime NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=26 DEFAULT CHARSET=utf8;

