-- ----------------------------
-- Table structure for recommend
-- ----------------------------
DROP TABLE IF EXISTS `recommend`;
CREATE TABLE `recommend` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '推荐编号',
  `userId` varchar(13) NOT NULL COMMENT '用户编号',
  `bookId` int(11) NOT NULL COMMENT '图书编号',
  `time` datetime NOT NULL COMMENT '推荐时间',
  `money` int(3) NOT NULL DEFAULT '0' COMMENT '积分数',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of recommend
-- ----------------------------

-- ----------------------------
-- Table structure for secondtype
-- ----------------------------
DROP TABLE IF EXISTS `secondtype`;
CREATE TABLE `secondtype` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '类型编号',
  `firstTypeId` int(11) NOT NULL COMMENT '一级类型编号',
  `name` varchar(20) NOT NULL COMMENT '类型名',
  `photo` varchar(255) DEFAULT NULL COMMENT '类型图片',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=75 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

