-- ----------------------------
-- View structure for bookview
-- ----------------------------
DROP VIEW IF EXISTS `bookview`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `bookview` AS select `author`.`phone` AS `authorId`,`author`.`name` AS `authorName`,`author`.`head` AS `head`,`author`.`profile` AS `authorProfile`,`book`.`id` AS `bookId`,`book`.`name` AS `bookName`,`book`.`label` AS `label`,`book`.`wordNum` AS `wordNum`,`book`.`collectionNum` AS `collectionNum`,`book`.`state` AS `state`,`book`.`profile` AS `profile`,`book`.`clickNum` AS `clickNum`,`book`.`cover` AS `cover`,`book`.`creationTime` AS `creationTime`,`book`.`chapterId` AS `chapterId`,`chapter`.`name` AS `chapterName`,`chapter`.`time` AS `chapterTime`,`secondtype`.`id` AS `secondId`,`secondtype`.`name` AS `secondName`,`firsttype`.`id` AS `firstId`,`firsttype`.`name` AS `firstName`,`book`.`recommendNum` AS `recommendNum`,`book`.`isRecommend` AS `isRecommend` from ((((`author` join `book` on((`author`.`phone` = `book`.`authorId`))) left join `chapter` on((`book`.`chapterId` = `chapter`.`id`))) join `secondtype` on(((`book`.`typeId` = `secondtype`.`id`) and ('' = '')))) join `firsttype` on((`secondtype`.`firstTypeId` = `firsttype`.`id`))) ;

-- ----------------------------
-- View structure for collectionview
-- ----------------------------
DROP VIEW IF EXISTS `collectionview`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `collectionview` AS select `collection`.`id` AS `id`,`collection`.`userId` AS `userId`,`collection`.`bookId` AS `bookId`,`collection`.`historyChapter` AS `historyChapter`,`chapter`.`name` AS `chapterName`,`author`.`name` AS `authorName`,`book`.`name` AS `bookName`,`secondtype`.`name` AS `secondName`,`firsttype`.`name` AS `firstName`,`book`.`state` AS `state`,`book`.`profile` AS `profile`,`book`.`wordNum` AS `wordNum`,`book`.`collectionNum` AS `collectionNum`,`book`.`clickNum` AS `clickNum`,`book`.`cover` AS `cover` from (((((`collection` join `book` on((`collection`.`bookId` = `book`.`id`))) left join `chapter` on((`collection`.`historyChapter` = `chapter`.`id`))) join `author` on((`book`.`authorId` = `author`.`phone`))) join `secondtype` on((`book`.`typeId` = `secondtype`.`id`))) join `firsttype` on((`secondtype`.`firstTypeId` = `firsttype`.`id`))) ;

-- ----------------------------
-- View structure for commentview
-- ----------------------------
DROP VIEW IF EXISTS `commentview`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `commentview` AS select `comment`.`id` AS `id`,`comment`.`reviewerId` AS `reviewerId`,`comment`.`bookId` AS `bookId`,`comment`.`content` AS `content`,`comment`.`time` AS `time`,`comment`.`praiseNum` AS `praiseNum`,`user`.`name` AS `name`,`user`.`head` AS `head`,`book`.`name` AS `bookName` from ((`comment` join `user` on((`comment`.`reviewerId` = `user`.`phone`))) join `book` on((`comment`.`bookId` = `book`.`id`))) ;
